import {Container,Row,Pagination, Col} from 'react-bootstrap';
import CarsCard from './CarsCard.js'
import axios from 'axios';
import { useEffect, useState } from 'react';

const api = axios.create({ baseURL: 'http://localhost:2020/api/v1/' });

const Car = () => {



    const [cars, setCar ] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

  useEffect (() => {
    const Car = async (req,res) => {
      await api.get('cars?page='+currentPage+'&limit=4').then(res => {
        setCar(res.data.vcars);
        setTotalPages(res.data.count);

      })
    }
    Car();
  },[currentPage]);


    const cardList=cars.map(car => <CarsCard key={car.titre} titre={car.titre} desc={car.desc}/>)

let items = [];
for (let number = 1; number <= totalPages; number++) {
  items.push(
    <Pagination.Item onClick={() => setCurrentPage(number)} key={number} active={number === currentPage}>
      {number}
    </Pagination.Item>,
  );
}

    return (
      <div></div>
    )
}

export default Car
