import {Container,Row,Pagination, Col} from 'react-bootstrap';
import CarsCard from './CarsCard.js'
import axios from 'axios';
import { useEffect, useState } from 'react';

const api = axios.create({ baseURL: 'http://localhost:2020/api/v1/' });

const Games = () => {



    const [cars, setGames ] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

  useEffect (() => {
    const Games = async (req,res) => {
      await api.get('cars?page='+currentPage+'&limit=4').then(res => {
        setGames(res.data.vcars);
        setTotalPages(res.data.count);

      })
    }
    Games();
  },[currentPage]);


    const cardList=cars.map(car => <CarsCard key={car.titre} titre={car.titre} desc={car.desc}/>)

let items = [];
for (let number = 1; number <= totalPages; number++) {
  items.push(
    <Pagination.Item onClick={() => setCurrentPage(number)} key={number} active={number === currentPage}>
      {number}
    </Pagination.Item>,
  );
}

    return (
      <div className='bg1'>
        <Container gap={3}>
          <hr />
            <h2 className='text-warning'>List of Games</h2>
            <Row>
                {cardList}

            </Row>
            <Row >
                <Col className='md-3 offset-6'>
                      <Pagination size="sm">{items}</Pagination>
                </Col>
            </Row>
            <hr />
        </Container>
      </div>
    )
}

export default Games
