import axios from 'axios';
import { useContext } from 'react'
import { Container } from 'react-bootstrap';
import { useForm } from "react-hook-form";
import { AuthenticateContext } from './AuthContext';
import { useNavigate } from 'react-router-dom'

const api = axios.create({ baseURL: 'http://localhost:2020/api/v1/' });

export default function Auth() {


    return (
        <>
        <Container>
            <h1 className='text-succes'>Login</h1>
            <form onSubmit={handleSubmit(Submit)}>
                <div className='form-group'>
                    <label htmlFor='email'>Email</label>
                    <input {...register('email')} type='email' className='form-control' id='email' name='email'/>
                </div>
                <div className='form-group'>
                    <label htmlFor='password'>Password</label>
                    <input {...register('password')} type='password' className='form-control' id='password'/>
                </div>
                <button type='submit' className='btn btn-primary mt-2'>Login</button>
            </form>
        </Container>
        </>
    )
}
